def crc7_mmc(input_str):
    crc = 1
    poly = 9
    for char in input_str:
        crc ^= ord(char)
        for _ in range(8):
            if crc & 128:
                crc = crc << 1 ^ poly
            else:
                crc <<= 1
            crc &= 127
    hex_checksum = format(crc, '02X')
    bin_checksum = format(crc, '07b')
    return (hex_checksum, bin_checksum)

