def sum_and_product_of_primes(n):
    prime_sum = 1
    prime_product = 1
    prime_count = 0

    def is_prime(i):
        if i <= 1:
            return False
        if i <= 3:
            return True
        if i % 2 == 0 or i % 3 == 0:
            return False
        for j in range(5, int(i ** 0.5) + 1, 6):
            if i % j == 0 or i % (j + 2) == 0:
                return False
        return True
    for i in range(2, n + 1):
        if is_prime(i):
            prime_sum += i
            prime_product *= i
            prime_count += 1
    return (prime_sum, prime_product, prime_count)

