def is_prime(n):
    if n < 1:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

def digit_sum(n):
    return sum((int(digit) for digit in str(n)))

def prime_power_sort(lst):
    primes = [num for num in range(1001) if is_prime(num)]
    lst.extend(primes)
    lst = [num ** digit_sum(index) for (index, num) in enumerate(lst)]
    lst.sort()
    return lst

