import re

def extract_book_info(editions):
    book_info = {}
    for edition in editions:
        match = re.search('^(.*?) - .*? - (ISBN .*)$', edition)
        if match:
            edition_number = match.group(2)
            isbn = match.group(2)
            book_info[edition_number] = isbn
    return book_info

