def is_valid_date(date_str):
    try:
        (day, month, year) = map(int, date_str.split('/'))
        if year <= 1:
            return 'Invalid date'
        if month < 1 or month > 12:
            return 'Invalid date'
        if month in [4, 6, 9, 11] and day > 30:
            return 'Invalid date'
        if month == 2:
            if year % 4 == 0 and year % 100 != 0 or year % 400 == 0:
                if day > 29:
                    return 'Invalid date'
            elif day > 28:
                return 'Invalid date'
        elif day > 31:
            return 'Invalid date'
        return 'Valid date'
    except ValueError:
        return 'Invalid date'

