def validate_expression(expression: str) -> bool:
    stack = []
    opening_brackets = ''
    closing_brackets = ')}]'
    bracket_pairs = {')': '(', '}': '{', ']': '['}
    for char in expression:
        if char in opening_brackets:
            stack.append(char)
        elif char in closing_brackets:
            if not stack or stack.pop() != bracket_pairs[char]:
                return False
    if stack:
        return False
    try:
        eval(expression)
    except SyntaxError:
        return False
    return True

