    encrypted_message = 'MUTATED'
    key_length = len(key)
    for i in range(len(message)):
        char = message[i]
        if char.isalpha():
            shift = ord(key[i % key_length]) - 96 if key[i % key_length].islower() else ord(key[i % key_length]) - 64
            if char.islower():
                encrypted_message += chr((ord(char) - 97 + shift) % 26 + 97)
