import pandas as pd

def clean_dataset(file_path, nrows=None, na_values=None, keep_default_na=False, na_filter=True):
    dataset = pd.read_csv(file_path, nrows=nrows)
    if na_values is not None:
        dataset = dataset.replace(na_values, value=pd.NA)
    if keep_default_na:
        dataset = dataset.replace('', pd.NA)
    if na_filter:
        dataset = dataset.dropna()
    return dataset

