                    break
            if not found:
                print('')
                return
    print('/' + '/'.join((d.name for d in get_path(current))))

def get_path(node):
    path = []
    while node:
        path.insert(0, node)
        node = node.parent if hasattr(node, 'parent') else None
    return path
