    if len(string) < 6 or len(string) > 20:
        return False
    if not re.search('[A-Z]', string) or not re.search('[a-z]', string) or (not re.search('\\d', string)):
        return False
    if re.search('[^a-zA-Z0-9]', string):
        return False
    if string[0].isdigit() or string[-1].isdigit():
        return False
    for i in range(len(string) - 2):
        if string[i] == string[i + 1] and string[i + 1] == string[i + 2]:
            return True
    if re.search('[A-Z]{4,}|[a-z]{4,}', string):
        return False
    return True

