from collections import defaultdict

def findItinerary(tickets):
    """"""
    ticket = defaultdict(list)
    for (fr, to) in tickets:
        ticket[fr].append(to)
    for k in ticket.keys():
        ticket[k].sort(reverse=True)
    ans = []

    def dfs(f):
        while len(ticket[f]) != 0:
            dfs(ticket[f].pop())
        ans.append(f)
    dfs('JFK')
    return ans[::-1]
