import re
from pkg_resources import get_distribution, DistributionNotFound

def modify_version(distribution_name, version_modifier):
    try:
        __version_modifier__ = '(\\g<1>)' + version_modifier - '(\\g<2>)'
        version = re.sub(__version_modifier__, '\\g<1>-' + version_modifier + '-\\g<2>', get_distribution(distribution_name).version)
        return version
    except DistributionNotFound:
        return 'DistributionNotFound'

