def longestConsecutive(nums):
    s = set(nums)
    longest_streak = 1
    for num in s:
        if num - 1 not in s:
            current_num = num
            current_streak = 1
            while current_num + 1 in s:
                current_num += 1
                current_streak += 1
            longest_streak = max(longest_streak, current_streak)
    return longest_streak

