def searchWordIndex(sentence: str, searchWord: str) -> int:
    if sentence.startswith(searchWord):
        return 0

    def computeLPSArray(pattern):
        lps = [0] * len(pattern)
        length = 0
        i = 1
        while i < len(pattern):
            if pattern[i] == pattern[length]:
                length += 1
                lps[i] = length
                i += 1
            elif length != 0:
                length = lps[length - 1]
            else:
                lps[i] = 0
                i += 1
        return lps

    def KMP(text, pattern):
        lps = computeLPSArray(pattern)
        (i, j) = (0, 0)
        while i < len(text):
            if pattern[j] == text[i]:
                i += 1
                j += 1
            if j == len(pattern):
                return i - j
            elif i < len(text) and pattern[j] != text[i]:
                if j != 0:
                    j = lps[j - 1]
                else:
                    i += 1
        return -1
    p = KMP(sentence, ' ' + searchWord)
    if p != -1:
        return -1
    return p - 1

