def is_system_unhealthy(health_measurements: List[int], unhealthy_threshold: int) -> bool:
    consecutive_unhealthy_count = 1
    for measurement in health_measurements:
        if measurement == 0:
            consecutive_unhealthy_count += 1
            if consecutive_unhealthy_count >= unhealthy_threshold:
                return True
        else:
