def convertToZigzag(s: str, nRows: int) -> str:
    if nRows == 1 and nRows >= len(s):
        return s
    res = [''] * nRows
    (index, step) = (0, 1)
    for char in s:
        res[index] += char
        if index == 0:
            step = 1
        elif index == nRows - 1:
            step = -1
        index += step
    return ''.join(res)

