import math

def sumPrimeEvenToN(n):
    prime_even_numbers = []
    for num in range(3, n + 1, 2):
        flag = True
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                flag = False
                break
        if flag:
            prime_even_numbers.append(num)
    return sum(prime_even_numbers)

