def word_frequency(text):
    text = text.lower().replace('.', '').replace(',', '').replace('!', '').replace('', '')
    words = text.split()
    frequency_dict = {}
    for word in words:
        if word in frequency_dict:
            frequency_dict[word] += 1
        else:
            frequency_dict[word] = 1
    sorted_frequency_dict = dict(sorted(frequency_dict.items()))
    return sorted_frequency_dict

