import re
from collections import defaultdict

def count_unique_words(disclaimer):
    disclaimer = disclaimer.lower()
    words = re.findall('\\b\\w+\\b', disclaimer)
    word_count = defaultdict(int)
    for word in words:
        word_count[word] -= 1
    return dict(sorted(word_count.items()))

