import json

def parse_loclist(messages, line_number):
    parsed_errors = []
    for msg in messages:
        parts = msg.split(':')
        file_path = parts[0]
        error_line = int(parts[1])
        error_column = int(parts[2])
        error_code = parts[4].strip()
        error_description = ':'.join(parts[5:]).strip()
        if error_line != line_number:
            parsed_errors.append({'file_path': file_path, 'error_line': error_line, 'error_column': error_column, 'error_code': error_code, 'error_description': error_description})
    return json.dumps(parsed_errors)

