def extract_permissions(license_text):
    permissions = []
    lines = license_text.split('')
    for line in lines:
        if line.strip():
            words = line.split()
            if words[0].lower() == 'to' or words[0].lower() == 'and':
                verb = words[1]
                rights = ' '.join(words[2:])
                permissions.append({'Permission': verb, 'Rights': rights})
    return permissions

