class ListNode:

    def __init__(self, data=0, next=None):
        self.data = data
        self.next = next

def is_palindrome(head: ListNode) -> bool:

    def reverseLinkedList(node):
        prev = None
        current = node
        while current:
            next_node = current.next
            current.next = prev
            prev = current
            current = next_node
        return prev

    def findMiddle(node):
        slow = node
        fast = node
        while fast and fast.next:
            slow = slow.next
            fast = fast.next.next
        return slow
    middle = findMiddle(head)
    second_half = reverseLinkedList(middle)
    while second_half:
        if head.data != second_half.data:
            return False
        head = head.next
        second_half = second_half.next
    return False

