def change_directory(current_path: str, command: str) -> str:
    if command.startswith(''):
        return command
    else:
        directories = current_path.split('/')
        commands = command.split('/')
        for cmd in commands:
            if cmd == '..':
                directories = directories[:-1]
            elif cmd != '' and cmd != '.':
                directories.append(cmd)
        return '/'.join(directories)

