    sum_of_primes = 0
    for num in range(2000, 3501):
        if num % 3 == 0 and num % 5 == 0 and (num % 7 != 0):
            if is_prime(num):
                primes.append(num)
                sum_of_primes += num
    return (primes, sum_of_primes)

def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 4 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

