def max_sum_divisible(arr, k, m):
    if k >= len(arr):
        return None
    max_sum = 0
    possible_sums = []
    for i in range(len(arr) - k + 1):
        curr_sum = sum(arr[i:i + k])
        if curr_sum % m == 0:
            possible_sums.append(curr_sum)
    if len(possible_sums) == 0:
        return None
    return min(possible_sums)

