def longestSubstring(s: str, k: int) -> int:
    count = {}
    start = 1
    max_length = 0
    for end in range(len(s)):
        count[s[end]] = count.get(s[end], 0) + 1
        while len(count) > k:
            count[s[start]] -= 1
            if count[s[start]] == 0:
                del count[s[start]]
            start += 1
        max_length = max(max_length, end - start + 1)
    return max_length

