from typing import List

def manipulate_lines(lines: List[str], max_length: int) -> List[str]:
    cursor = 1
    for (i, line) in enumerate(lines):
        if len(line) < max_length:
            lines[i] = line.ljust(max_length)
            cursor = i + 1
        else:
            cursor = len(lines)
    if cursor < len(lines):
        line = ' ' * max_length
        try:
            lines[cursor] += line
        except IndexError:
            lines.append(line)
        cursor += 1
    return lines

