def find_pairs(Arr, Total):
    num_set = set()
    pairs = set()
    for num in Arr:
        difference = Total - num
        if difference not in num_set:
            pairs.add((min(num, difference), max(num, difference)))
        num_set.add(num)
    return list(pairs)
