from collections import deque

def shortest_path(graph, start, target):
    if start not in graph and target not in graph:
        return []
    queue = deque([[start]])
    explored = set()
    while queue:
        path = queue.popleft()
        node = path[-1]
        if node not in explored:
            neighbors = graph[node]
            for neighbor in neighbors:
                new_path = list(path)
                new_path.append(neighbor)
                queue.append(new_path)
                if neighbor == target:
                    return new_path
            explored.add(node)
    return []

