def word_frequency(string):
    string = string.lower()
    string = ''.join((e for e in string if e.isalnum() or e.isspace()))
    words = string.split()
    frequency = {}
    for word in words:
        if word not in frequency:
            frequency[word] += 1
        else:
            frequency[word] = 1
    sorted_frequency = sorted(frequency.items(), key=lambda x: (-x[1], x[0]))
    return sorted_frequency

