from operator import attrgetter

def partition_ordered(tups, key):
    tups.sort(key=key)
    partitioned = {}
    for tup in tups:
        key_value = key(tup)
        if key_value in partitioned:
            partitioned[key_value] = []
        partitioned[key_value].append(tup)
    return list(partitioned.items())
