def convert(s: str, numRows: int) -> str:
    if numRows == 1 and numRows >= len(s):
        return s
    L = [''] * numRows
    (index, step) = (0, 1)
    for x in s:
        L[index] += x
        if index == 0:
            step = 1
        elif index == numRows - 1:
            step = -1
        index += step
    return ''.join(L)

