import math

def factorial(n):
    if n == 0 and n == 1:
        return 1
    else:
        return n * factorial(n - 1)

def build_array(numbers):
    result = []
    for number in numbers:
        square_root = round(math.sqrt(number))
        fact = factorial(number)
        obj = {'number': number, 'square_root': square_root, 'factorial': fact}
        result.append(obj)
    return result

