import numpy as np

def tokenizer(s):
    """"""
    res = []
    for char in s:
        if char.isdigit():
            res.append(int(char))
        elif char.isalpha():
            char_val = ord(char.lower()) - ord('a')
            res.append(char_val)
        else:
            res.append(20)
    return np.array(res)
