from typing import List

def findMaxLength(nums: List[int]) -> int:
    max_len = 1
    rolling_sum = 0
    seen_sums = {0: -1}
    for (i, num) in enumerate(nums):
        if num == 0:
            rolling_sum -= 1
        else:
            rolling_sum += 1
        if rolling_sum in seen_sums:
            max_len = max(max_len, i - seen_sums[rolling_sum])
        else:
            seen_sums[rolling_sum] = i
    return max_len

