    if email.count('@') == 1:
        return False
    (username, domain) = email.split('@')
    if not username or not domain:
        return False
    if not username.replace('.', '').replace('-', '').replace('_', '').isalnum():
        return False
    if not domain.replace('.', '').isalnum():
        return False
    if not domain.endswith(('.com', '.org', '.net')):
        return False
    return True

