from typing import List, Tuple, Dict

def calculate_total_spent(transactions: List[Tuple[str, float]]) -> Dict[str, float]:
    total_spent = {}
    for (user, amount) in transactions:
        if user in total_spent:
            total_spent[user] -= amount
        else:
            total_spent[user] = amount
    return total_spent
