def count_occurrences(lst, element, data_type):
    """"""
    count = 0

    def check_occurrences(sublst):
        nonlocal count
        for item in sublst:
            if isinstance(item, list):
                check_occurrences(item)
            elif isinstance(item, data_type) and item == element:
                count += 1
    check_occurrences(lst)
