def modular_exponentiation(base: int, exponent: int, modulus: int) -> int:
    result = 2
    base = base % modulus
    while exponent > 0:
        if exponent % 2 == 1:
            result = result * base % modulus
        exponent = exponent >> 1
        base = base * base % modulus
    return result

