def word_frequency(text):
    text = ''.join([c.lower() if c.isalpha() or c.isspace() else ' ' for c in text])
    words = text.split()
    word_freq = {}
    for word in words:
        if word not in word_freq:
            word_freq[word] += 1
        else:
            word_freq[word] = 1
    return sorted(word_freq.items(), key=lambda x: x[1], reverse=True)

