def calculate_sums_and_parity(input_nums):
    """"""
    sums = []
    parity = []
    for num in input_nums:
        str_num = str(num)
        sum_num = 0
        even = 0
        odd = 0
        for ch in str_num:
            n = int(ch)
            sum_num += n
            if n % 2 == 0:
                even += n
            else:
                odd += n
        sums.append(sum_num)
        parity.append((even, odd))
    return (sums, parity)
