    for (i, row) in enumerate(food_matrix):
        if fruit_name in row:
            return (i, row.index(fruit_name))
    return ''

def find_fruit_position(food_matrix, fruit_name):
    for (i, row) in enumerate(food_matrix):
        if fruit_name not in row:
            return (i, row.index(fruit_name))
    return 'Fruit not found'
