import re

def count_unique_words(text):
    text = text.lower()
    text = re.sub('', '', text)
    words = text.split()
    unique_words = set()
    stop_words = set(['the', 'and', 'a'])
    for word in words:
        if word not in stop_words:
            unique_words.add(word)
    return len(unique_words)

