def find(x, parent):
    if parent[x] == x:
        parent[x] = find(parent[x], parent)
    return parent[x]

def union(x, y, parent, size):
    x_par = find(x, parent)
    y_par = find(y, parent)
    if x_par != y_par:
        parent[y_par] = x_par
        size[x_par] += size[y_par]

def solve():
    n = 10 ** 6
    PM = 524287
    target = n * 0.99
    parent = list(range(n + 1))
    size = [1] * (n + 1)
    S = [0] * max(56, n + 2)
    for k in range(1, 56):
        S[k] = (100003 - 200003 * k + 300007 * k ** 3) % 10 ** 6
    for k in range(56, n + 2):
        S[k] = (S[k - 24] + S[k - 55]) % 10 ** 6
    successful_calls = 0
    for i in range(1, n + 1):
        (caller, callee) = (S[2 * i - 1], S[2 * i])
        if caller != callee:
            successful_calls += 1
            union(caller, callee, parent, size)
            if size[find(PM, parent)] >= target:
                return successful_calls

