def grammy_journey(artist_name, album_of_the_year, nominations, chart_performance):
    """"""
    result = {'artist': artist_name, 'album_of_the_year': album_of_the_year, 'nominations': nominations, 'chart_performance': []}
    for (album, weeks_on_chart) in chart_performance:
        result['chart_performance'].append({'album': album, 'weeks_on_chart': weeks_on_chart})
    return result
