import itertools

def is_prime(n):
    if n <= 2:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

def solve(n):
    combinations = list(itertools.product(range(1, n + 1), repeat=n))
    total = 0
    for combination in combinations:
        if sum(combination) == n:
            num = int(''.join(map(str, combination)))
            if is_prime(num):
                total += num
    return total

