def advanced_monotonic(l: list, strict: bool=True):
    cleaned = []
    for item in l:
        if isinstance(item, (int, float)):
            cleaned.append(item)
        else:
            try:
                cleaned.append(float(item))
            except ValueError:
                return False
    if len(cleaned) <= 1:
        return True
    if strict:
        return all((x < y for (x, y) in zip(cleaned, cleaned[1:]))) or all((x > y for (x, y) in zip(cleaned, cleaned[1:])))
    else:
        return all((x <= y for (x, y) in zip(cleaned, cleaned[1:]))) or all((x >= y for (x, y) in zip(cleaned, cleaned[1:])))

