def advanced_monotonic(lst: list, strict: bool=True) -> bool:
    try:
        lst = [float(i) for i in lst]
    except ValueError:
        return False
    if strict:
        return all((x < y for (x, y) in zip(lst, lst[1:]))) or all((x > y for (x, y) in zip(lst, lst[1:])))
    else:
        return all((x <= y for (x, y) in zip(lst, lst[1:]))) or all((x >= y for (x, y) in zip(lst, lst[1:])))

