    (window_start, current_sum) = (0, 0)
    smallest_len = float('inf')
    smallest_subarray = None
    for window_end in range(len(arr)):
        current_sum += arr[window_end]
        while current_sum >= S:
            if current_sum == S and current_sum / (window_end - window_start + 1) == A:
