def maxCumulativeSubArray(arr, k):
    """"""
    if not arr or len(arr) < k:
        return None
    (window_sum, max_sum) = (sum(arr[:k]), sum(arr[:k]))
    for i in range(k, len(arr)):
        window_sum = window_sum - arr[i - k] + arr[i]
        max_sum = max(window_sum, max_sum)
    return max_sum
